import React from 'react';
import 'antd/dist/antd.css';
import '../App.css'
import { Table, Tag, Space } from 'antd';

const { Column } = Table;

const data = [
  {
    key: '1',
    ranking: '1',
    teamName: 'Team 1',
    scores: 80000,
    tags: ['Golden'],
  },
  {
    key: '2',
    ranking: '2',
    teamName: 'Team 2',
    scores: 70000,
    tags: ['Silver'],
  },
  {
    key: '3',
    ranking: '3',
    teamName: 'Team 3',
    scores: 50000,
    tags: ['Bronze'],
  },
  {
    key: '4',
    ranking: '4',
    teamName: 'Team 3',
    scores: 10000,
    tags: ['4th place'],
  },
];
class Team extends React.Component {
  render() {
    return(
<Table
      columns={columns}
      dataSource={data}
      pagination={false}
      bordered
      summary={pageData => {
        let totalBorrow = 0;
        let totalRepayment = 0;

        pageData.forEach(({ borrow, repayment }) => {
          totalBorrow += borrow;
          totalRepayment += repayment;
        });

        return (
          <>
            <Table.Summary.Row>
              <Table.Summary.Cell>Total</Table.Summary.Cell>
              <Table.Summary.Cell>
                <Text type="danger">{totalBorrow}</Text>
              </Table.Summary.Cell>
              <Table.Summary.Cell>
                <Text>{totalRepayment}</Text>
              </Table.Summary.Cell>
            </Table.Summary.Row>
            <Table.Summary.Row>
              <Table.Summary.Cell>Balance</Table.Summary.Cell>
              <Table.Summary.Cell colSpan={2}>
                <Text type="danger">{totalBorrow - totalRepayment}</Text>
              </Table.Summary.Cell>
            </Table.Summary.Row>
          </>
        );
      }}
    />
    );
  }
}

 
export default Team;
